  constructor(render, storage = "media-player-id-5l4yI5jyncpJXZfW") {
    const self = MediaPlayer;

    this.__element = self.__template();

    this.__elements = self.__script({
      element: this.__element,
      storage: storage,
    });

    this.__storage = storage;

    render.append(this.__element);
  }

  __constructor = () => {};

  info = (data = "") => {
    this.__elements.videoTitle.textContent = data?.title || "";
    this.__elements.videoDescription.textContent = data?.description || "";
  };

  controls = (data) => {
    if (data?.options) {
      const buttons = Array.from(
        this.__elements.divOptionsButtons.querySelectorAll("button")
      );
      Object.entries(data?.options).forEach((entries) => {
        buttons.forEach((button) => {
          if (
            entries[1].includes(button.getAttribute("data-option-action")) ||
            entries[1].includes("*")
          ) {
            button.style.display = entries[0] == "yes" ? "flex" : "none";
          }
        });
      });
    }
  };

  video = (callback) => {
    if (typeof callback == "function") callback(this.__elements.video);
  };

  element = (query = "") => {
    return query != "" ? this.__element.querySelector(query) : this.__element;
  };
